import org.slf4j.Logger
import scala.collection.mutable
import scala.io.Source
import scala.language.implicitConversions
//SciSpark imports
import org.dia.Parsers
import org.dia.core.{ SciSparkContext, SciTensor }
import org.dia.algorithms.mcc.MCCOps

// the user variables
val partCount = if (args.length <= 1) 2 else args(0).toInt
val varName = if (args.length <= 2) "ch4" else args(1)
val hdfspath = if (args.length <= 3) args(2) else "hdfs://128.149.112.134:8090/data/July2006"

// Initialize the spark context 
val dssc = new SciSparkContext(sc)

val componentFrameRDD1 = consecFrames1.flatMap({
    case (t1, t2) => {
        val (components1, _) = MCCOps.labelConnectedComponents(t1.tensor)
        val (components2, _) = MCCOps.labelConnectedComponents(t2.tensor)
        val product1 = components1 * components2
        var overlappedPairsList1 = mutable.MutableList[(Double, Double)]()
        var areaPairsList1 = mutable.MutableList[(String, String)]()
        var areaMaxMinTable1 = new mutable.HashMap[String, (Double, Double, Double)]

    def updateComponent(label: Double, frame: String, value: Double): Unit = {
        if (label != 0.0) {
            var area = 0.0
            var max = Double.MaxValue
            var min = Double.MinValue
            val currentProperties1 = areaMaxMinTable1.get(frame + ":" + label)
            if (currentProperties1 != null && currentProperties1.isDefined) {
              area = currentProperties1.get._1
              max = currentProperties1.get._2
              min = currentProperties1.get._3
              if (value < min) min = value
              if (value > max) max = value
            } else {
              min = value
              max = value
            }
            area += 1
            areaMaxMinTable1 += ((frame + ":" + label, (area, max, min)))
          }
        }

        for (row <- 0 to product1.rows - 1) {
          for (col <- 0 to product1.cols - 1) {
            if (product1(row, col) != 0.0) {
              val value1 = components1(row, col)
              val value2 = components2(row, col)
              overlappedPairsList1 += ((value1, value2))
            }
            updateComponent(components1(row, col), t1.metaData("FRAME"), t1.tensor(row, col))
            updateComponent(components2(row, col), t2.metaData("FRAME"), t2.tensor(row, col))
          }
        }

    val edgesSet1 = overlappedPairsList1.toSet
    val edges1 = edgesSet1.map({ case (c1, c2) => ((t1.metaData("FRAME"),c1), (t2.metaData("FRAME"), c2)) })

    val filtered1 = edges1.filter({
          case ((frameId1, compId1), (frameId2, compId2)) => {
            val (area1, max1, min1) = areaMaxMinTable1(frameId1 + ":" + compId1)
            val isCloud1 = ((area1 >= 2400.0) || ((area1 < 2400.0) && ((min1/max1) < 0.9)))
            val (area2, max2, min2) = areaMaxMinTable1(frameId2 + ":" + compId2)
            val isCloud2 = ((area2 >= 2400.0) || ((area2 < 2400.0) && ((min2/max2) < 0.9)))
            isCloud1 && isCloud2
          }
        })

    val addarea = filtered1.map({ 
        case (m1 , m2) => ((m1._1, m1._2, areaMaxMinTable1(m1._1 + ":" + m1._2)._1), (m2._1, m2._2, areaMaxMinTable1(m2._1 + ":" + m2._2)._1)) 
    })
        addarea
      }
    })
