def getLatBandForMaxStage(latmax: Double, startLat: Double, latWidth:Double, stage:Char): List[(String, Double, Double, List[Double], List[String])] = {
    var currLat = startLat
    val maxCountTuple = new mutable.ListBuffer[(String, Double, Double, List[Double], List[String])] //(devStage, currLat, count, List(lonMin, lonMax), List(cloudelements))
    var maxCount = 0.0
    var currCountM = 0.0
    val currStage = 'M'
    while (currLat <= (latmax - latWidth)){
        var topLimit = currLat + latWidth 
        // var curr = sqlContext.sql("SELECT frameNum, cloudElemt, centerLat, centerLon, devStage FROM ce_table WHERE (devStage == "+currStage+") AND (centerLat >= "+currLat+" AND centerLat <= " + topLimit +")")
        var curr = sqlContext.sql("SELECT frameNum, cloudElemt, centerLat, centerLon, devStage FROM ce_table WHERE (centerLat >= "+currLat+" AND centerLat <= " + topLimit +") AND devStage == 'M'")
        currCountM = curr.count

        if (currCountM > maxCount){
            // empty exisiting list and add this one only
            for (i <-0 to maxCountTuple.length -1){
                maxCountTuple.remove(i)
            }
            var lonMin = curr.groupBy("devStage").min("centerLon").collect().toList(0)(1).asInstanceOf[Double]
            var lonMax = curr.groupBy("devStage").max("centerLon").collect().toList(0)(1).asInstanceOf[Double]
            maxCountTuple += ((String.valueOf(currStage), currLat, currCountM, List(lonMin,lonMax), curr.select("frameNum", "cloudElemt").rdd.map(r => r(0)+":"+r(1)).collect().toList))
            maxCount = currCountM
        } else if (currCountM == maxCount){
            var lonMin = curr.groupBy("devStage").min("centerLon").collect().toList(0)(1).asInstanceOf[Double]
            var lonMax = curr.groupBy("devStage").max("centerLon").collect().toList(0)(1).asInstanceOf[Double]
            maxCountTuple += ((String.valueOf(currStage), currLat, currCountM, List(lonMin,lonMax), curr.select("frameNum", "cloudElemt").rdd.map(r => r(0)+":"+r(1)).collect().toList))
            maxCount = currCountM
        }
        currLat = currLat + 1.0
    }
    maxCountTuple.toList
}

