def generate_node_data(workdir, mcsNodeMap2n):
    nodeData = []
    keyorder = ['nodeID', 'centerLat', 'centerLon', 'area', 'minTemp', 'minArea', 'minAreaTemp', 'minTempArea' , 'maxTemp', 'maxTempArea', 'latMin', 'latMax', 'lonMin', 'lonMax', 'lightningData', 'devStage', 'thumbnail']

    for aLine in mcsNodeMap2n:
        try:
            data1 = json.loads(aLine)
            data1.pop("grid")
            data1.pop("metadata")
            data1[u"nodeID"] = "F"+str(data1["frameNum"])+"CE"+str(data1["cloudElemNum"])[:-2]
            data1[u"thumbnail"] = workdir + "pngs/" + "F"+str(data1["frameNum"])+"CE"+str(data1["cloudElemNum"])[:-2]+".png"
            data1.pop("rowMax")
            data1.pop("rowMin")
            data1.pop("colMax")
            data1.pop("colMin")
            data1.pop("cloudElemNum")
            data1.pop("frameNum")
            data1.pop("maxTempArea")
            data1.pop("210TempArea")
            data1.pop("avgTemp")
            nodeData.append(OrderedDict(sorted(data1.items(), key=lambda i:keyorder.index(i[0]))))
        except:
            continue
    return nodeData

def add_nodes(graph, nodes):
    for n in nodes:
        if isinstance(n, tuple):
            graph.node(n[0], **n[1])
        else:
            graph.node(n)
    return graph

def add_edges(graph, edges):
    for e in edges:
        if isinstance(e[0], tuple):
            graph.edge(*e[0], **e[1])
        else:
            graph.edge(*e)
    return graph

def get_png_str(fileName):
    try:
        dataUri = open(fileName, 'rb').read().encode('base64').replace('\n','')
        imgTag = 'data:image/png;base64,' +dataUri
        return imgTag
    except:
        return fileName

def generate_svgs(maxLimit, edgeList, data, workdir):
    tuplesPy = [] 
    if "(" in edgeList[0]: #len(edgeList) > 1: # # or"
        for eLine in edgeList:
            eLine = eLine.replace("(","").replace(")","").split(", ")
            if eLine[-1] == '':
                eLine = eLine[:-1]
            for edge in eLine:
                edge = edge.split(",")
                src = 'F' + edge[0].split(":")[0] + 'CE' + edge[0].split(":")[1]
                dest = 'F' + edge[1].split(":")[0] + 'CE' + edge[1].split(":")[1]
                tuplesPy.append((src, dest))
    else:
        for i in range(0, len(edgeList)-2, 2): #for n in edgeList:
            src = 'F' + edgeList[i].split(":")[0].strip() + 'CE' + edgeList[i].split(":")[1].strip()
            dest = 'F' + edgeList[i+1].split(":")[0].strip() + 'CE' + edgeList[i+1].split(":")[1].strip()
            tuplesPy.append((src, dest))

    G = nx.DiGraph(tuplesPy)
    weakConnComponents = []
    for w in nx.weakly_connected_component_subgraphs(G):
        weakConnComponents.append(w.edges())

    # weakConnComponents is sorted by most connected graph based on the 'from' node i.e. most unique Frame numbers in a graph
    weakConnComponents.sort(key=lambda x: len(collections.Counter(y[0].split('CE')[0].split('F')[1] for y in x)), reverse=True)

    # iterate through all weakConnComponents here, lay them out one on top of another
    yPos = 0

    for i in range(0, int(maxLimit)):
        nodes = []
        for edgeTup in weakConnComponents[i]:
            if not any(filter (lambda x: x[0] == edgeTup[0], nodes)):
                nodeAttrDict = filter(lambda thisNode: thisNode[u'nodeID'] == edgeTup[0], data)[0]
                #create multiline tooltip dict with all user defined node attributes
                multiLine = ''
                tLine = ''
                for k,v in nodeAttrDict.iteritems():
                    if k == 'thumbnail':
                        tLine += str(v)
                    else:
                        multiLine += k + ' = ' + str(v) + '&#10;'
                nodes.append((edgeTup[0], {'tooltip':multiLine, 'image':tLine, 'label':"", }))

            if not any(filter (lambda x: x[0] == edgeTup[1], nodes)):
                nodeAttrDict = filter(lambda thisNode: thisNode['nodeID'] == edgeTup[1], data)[0]
                multiLine = ''
                tLine = ''
                for k,v in nodeAttrDict.iteritems():
                    if k == 'thumbnail':
                        tLine += str(v) 
                    else:
                        multiLine += k + ' = ' + str(v) + '&#10;'
                nodes.append((edgeTup[1], {'tooltip':multiLine, 'image':tLine, 'label':"", })) 

        #add fake node for legend
        print "add fake node"
        cbPng = workdir + 'pngs/colorbar.png'
        nodes.append(('', {'shape':'rectangle', 'image':cbPng, 'imagescale':'true', 'color':'white','label':"", }))

        edges = weakConnComponents[i]
        digraph = functools.partial(gv.Digraph, graph_attr={"rankdir": "LR", "ratio":"compress"}, format='svg') #actually creates the graph viz layout 
        print digraph
        add_edges(add_nodes(digraph(name='graph'+str(i+1)), nodes), edges).render(workdir+'img/g' + str(i))

        # Some cleaning up of the svgs generated for viz purposes.
        with open(workdir+'img/g{0}.svg'.format(i), 'r') as f:
            lineStr = f.read()
            lines = lineStr.splitlines()
            for line in lines:
                # Couldn't get the png to be read into the svg from the dot file.
                # Attempted adding directly to dot file as HTML string  label="<img src='data:image/png;base64,'%s'>" % dataUri
                # The problems were: (1) the <> were being removed and the '' were being converted to ASCII ie. &#39; 
                # This dirty solution is to open the generated svg, that is generated from the graphviz dot file where the img is 
                # represented with the image tag and update the URL with the pngStr
                if '<image xlink:href=' in line:
                    currPng = line.split('<image xlink:href="')[1].split('.png')[0] + '.png'
                    pngStr = get_png_str(currPng)
                    lineStr = lineStr.replace(currPng, pngStr)

                # Fix to disable clickablity of <a> tag generated in the svg with the tooltip option in graphViz.
                # More specificially, the <a> causes the node to be clickable and thus  an empty link is seen by the user. 
                # This dirty fix disables the <a> tag clickability in the generated svg by using HTML <a> option onclick="return false;"
                if '<a xlink:title' in line:
                   lineStr = lineStr.replace('<a xlink:title', '<a onclick="return false;" xlink:title')

                # Update as the graph id number is always graph1. TODO: check if this can be done with a flag
                if '<g id="graph1' in line:
                   lineStr = lineStr.replace('<g id="graph1', '<g id="graph{0}'.format(i+1))

        with open(workdir+'img/g{0}.svg'.format(i), 'w') as f:
            f.write(lineStr)


